-- -----------------------------------------------------
-- Data for table `FIPOP`.`change_in_tempo_translation`
-- -----------------------------------------------------
START TRANSACTION;
USE `FIPOP`;
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (1, 1, 1, 'Accelerando', '[wikipedia] speeding up (abbreviation: accel.)');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (2, 2, 1, 'Allargando', '[wikipedia] growing broader; decreasing tempo, usually near the end of a piece');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (3, 3, 1, 'Calando', '[wikipedia] going slower (and usually also softer)');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (4, 4, 1, 'Doppio movimento / doppio più mosso', '[wikipedia] double-speed');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (5, 5, 1, 'Doppio più lento', '[wikipedia] half-speed');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (6, 6, 1, 'Lentando', '[wikipedia] gradually slowing, and softer');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (7, 7, 1, 'Meno mosso', '[wikipedia] less movement; slower');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (8, 8, 1, 'Meno moto', '[wikipedia] less motion');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (9, 9, 1, 'Più mosso', '[wikipedia] more movement; faster');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (10, 10, 1, 'Mosso', '[wikipedia] movement, more lively; quicker, much like più mosso, but not as extreme');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (11, 11, 1, 'Precipitando', '[wikipedia] hurrying; going faster/forward');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (12, 12, 1, 'Rallentando', '[wikipedia] a gradual slowing down (abbreviation: rall.)');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (13, 13, 1, 'Ritardando', '[wikipedia] slowing down gradually; also see rallentando and ritenuto (abbreviations: rit., ritard.) sometimes replaces allargando.');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (14, 14, 1, 'Ritenuto', '[wikipedia] slightly slower, but achieved more immediately than rallentando or ritardando; a sudden decrease in tempo; temporarily holding back.[17] (Note that the abbreviation for ritenuto can also be rit. Thus a more specific abbreviation is riten. Also, sometimes ritenuto does not reflect a tempo change but rather a \'character\' change.)');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (15, 15, 1, 'Rubato', '[wikipedia] free adjustment of tempo for expressive purposes, literally \"theft\"—so more strictly, to take time from one beat to slow another');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (16, 16, 1, 'Stretto', '[wikipedia] in a faster tempo, often used near the conclusion of a section. (Note that in fugal compositions, the term stretto refers to the imitation of the subject in close succession, before the subject is completed, and as such, suitable for the close of the fugue.[18] Used in this context, the term is not necessarily related to tempo.)');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (17, 17, 1, 'Stringendo', '[wikipedia] pressing on faster, literally \"tightening\"');
INSERT INTO `FIPOP`.`change_in_tempo_translation` (`id`, `change_in_tempo_id`, `language_id`, `type`, `description`) VALUES (18, 18, 1, 'Tardando', '[wikipedia] slowing down gradually (same as ritardando)[19]');

COMMIT;


